elements.expression:
filters
| essql 
  query="SELECT
flight_date \"Flight Date\",
carrier \"Carrier\",
full_flight_number \"Flight Number\",
direction_desc \"Direction\",
origin \"Origin\",
destination \"Destination\",
message_type \"Message Type\",
count(message_type) \"Message Count\"

FROM \"message-flight-idx\"
GROUP BY flight_id_tag, 
flight_date,
message_type,
carrier, full_flight_number,
direction_desc,
origin,
destination

ORDER BY flight_date desc,
full_flight_number,
message_type 

"
| alterColumn "Flight Date" name="fl_date"
| mapColumn "fl_date" fn={getCell "fl_date" | formatdate "YYYY-MM-DD"}
| alterColumn "fl_date" name="Flight Date"
| table
| render containerStyle={containerStyle border="1px solid #1974D2"} 
  css=".canvasRenderEl{

}

.canvasDataTable__tbody>:nth-child(even) {
background-color:#e6f5ff;
}
.canvasDataTable__tbody>:nth-child(odd) {
background-color:#FFFFFF ;
}

.canvasDataTable__th {background-color: #1974D2; color: #FFFFFF; 
}"

