#!/bin/bash

function wait_for_file_to_be_writen {
    FILENAME=$1
    COUNTER=0
    NAPTIME=1
    while [  1 ]; do
        sleep "$NAPTIME"s
        let COUNTER=COUNTER+$NAPTIME
        if [[ -f "$FILENAME" ]] || [[ -d "$FILENAME" ]]
           then
                echo "It took $COUNTER seconds for $FILENAME to be writen to disk. Done waiting..."
                break
        fi
    done
}

