echo "Running Kaizen Auto ML Pipeline. This will evaluate the Kaizen models used for inference"

modelVersion=$1
derogThreshold=$2

cd $OMNI_HOME/.training_ground/scala

echo "Clear the previous performance metrics files"
trainMetricsDirectory=$OMNI_HOME/.training_ground/scala/src/data/output/processed/TRAIN_df_binarymetrics.json

testMetricsDirectory=$OMNI_HOME/.training_ground/scala/src/data/output/processed/TEST_df_binarymetrics.json

cmd=`\rm -rf $trainMetricsDirectory $testMetricsDirectory`

echo "Remove previous performance metrics command output=$cmd"

echo "Evaluating the model with a derog threshold of $derogThreshold"

run_scala_class_spark_231_two_params com.omni.paxspark.evaluation.evaluateModel_binary $derogThreshold

# Make sure the performance metrics files are fully writen to disk  models...
trainMetricsFilename=$OMNI_HOME/.training_ground/scala/src/data/output/processed/TRAIN_df_binarymetrics.json/part-*.json

testMetricsFilename=$OMNI_HOME/.training_ground/scala/src/data/output/processed/TEST_df_binarymetrics.json/part-*.json

wait_for_file_to_be_writen $trainMetricsFilename

wait_for_file_to_be_writen $testMetricsFilename

# Save the names of the performance json files so that the upper layer can read them...
