# echo $trainMetricsFilename > $performance_metrics_filenames_container

# echo $testMetricsFilename >> $performance_metrics_filenames_container

# echo "Create or updating model version performance folder if it does not exist"

# Take a copy of the performance metrics files for archiving purposes...
modelOutputFolder=/opt/omni/.model_performance_metrics/v$modelVersion

if [ -d "$modelOutputFolder" ]
then
    echo "Directory $modelOutputFolder exists."
    echo "Removing the content of the previous version of the model performance metrics."
    cmd=`\rm -rf $modelOutputFolder/*`
else
    echo "Directory $modelOutputFolder does not exists."
    echo "Creating the model version performance metrics."
    cmd=`\mkdir $modelOutputFolder`
fi
echo "Create/Update model version performance metrics folder=$cmd"

echo "Copy the generated model performance metrics to the model repository"
cmd=`\cp $trainMetricsFilename $modelOutputFolder/train_performance_metrics.json`

echo "Copied the train model performance metrics to the models performance directory command output=$cmd"

cmd=`\cp $testMetricsFilename $modelOutputFolder/test_performance_metrics.json`

echo "Copied the test model performance metrics to the models performance directory command output=$cmd"

#Give a chance to the performance metrics to be fully writen/moved before exiting...
sleep 30s

